def get_wiki_embedding(task):
    prompt_list = []
    for state in ALL_STATES:
        # generate natural language for e5 model
        prompt_list.append('Instruct: {}\nQuery: {}'.format(instruct[task], wiki[state]))
    # convert list to dataframe
    df = pd.DataFrame(prompt_list, columns=['input'])
    # generate embeddings
    embedding = embed_e5(df)
    np.save(f"/shared/share_mala/llm-dro/domain_info/wiki/{task}.npy", embedding)
    print(embedding.shape)

